import React, {useRef,useState} from "react";
import styled from 'styled-components';
import Reviews from "../../../static_data/review";


const ReviewViewStyle = styled.div`
.sample {
    margin: 0 auto;
}
@media screen and (min-width:958px ) {
    .sample {
        width: 80%;
    }
}
`;
const ContentBoxStyle = {
    border:'1px solid gray',
    width:'250px',
    height:'250px',
    margin: '25px',
    float:'none',
    //margin : '0 auto'
}
const FilterBtn = styled.div `
input {
    border: 1px solid #eeeeee;
    border-radius: 30px;
    outline:none;
}
button {
    width:70px;
}
@media screen and (max-width:958px ) {
    input {
        width: 400px;
    }
}
`;

function ContentBox() {
    return (        
        <div style={ContentBoxStyle}>
            <p>{review.id}</p>
            <p>{review.img}</p>
            <p>{review.title}</p>
            <p>{review.introduce}</p>
        </div>

    );
}
const title = "";
//props
function ReviewView() {
    const [text, setText] = useState('');
    const titleInput = useRef();
    const onClick = () => {
        title = titleInput.current.value;
        setText(title);
    }

    const naverUrl = 'https://search.naver.com/search.naver?where=nexearch&sm=top_hty&fbm=0&ie=utf8&query=';
    return (
        <>
        <FilterBtn>
        <div className={"row justify-content-center"}>
            <input ref={titleInput} className={"col-md-4 col-xs-5"} placeholder="후기가 궁금한 음식점을 검색해보세요."/>
            <button className={"col-md-1 col-xs-2"} onClick={onClick}>검색 </button>
        </div> 
     </FilterBtn>
        <p>{title}</p>
        <span><a href={naverUrl+title} target="_blank">title</a></span> 
        <ReviewViewStyle>
        <div className={"row justify-content-center sample"}>
            {Reviews.map((review) => {
               if(title==review.title||title==''){return <ContentBox review={review} key = {review.id}/>}
            })}
        </div>
        </ReviewViewStyle>
     </>
    );
}

export default ReviewView;
