  //   const response = await fetch('/api/restaurants');
  //   const body = await response.json();
  //   console.log(body);
  //   return body;
  // }
  return (
    
    <div className="App Container">
      <MenuBar restaurants = {restaurants}/>
    </div>
    
  ); }

export default App;
